package iterationstatement;

public class BreakStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		 for(int p=1;p<=10;p++) {
	            //it will print 1  2   3   4  5  6  7  8
	            
	            if(p==9) {
	                break;
	            }
	            
	            System.out.println("======="+p);
	            
	        }
		 
		
		
		
	}

}
