package iterationstatement;

public class LabAssignment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("The odd numbers between 1 to 50 are : \n" );
		
		int check = 0;
		
		for( int i=1; i<=50 ; i++) {
			
			check = i%2;// even if O
			
			if (check !=0) {
				
				System.out.println(i);
			
		}

	}


	}
}
	
